import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Palette, Music, BookOpen, Sparkles, Loader2 } from "lucide-react";
import { trackSandboxCreation } from "@/lib/usage";

interface CreatedContent {
  type: "art" | "story" | "music" | "tip";
  content: string;
  title: string;
}

export default function EmotionSandbox() {
  const [emotion, setEmotion] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const [createdContent, setCreatedContent] = useState<CreatedContent[]>([]);

  const generateContent = async () => {
    if (!emotion.trim()) return;

    setIsGenerating(true);

    // Simulate AI content generation (in real app, this would call OpenAI API)
    setTimeout(() => {
      const mockContent: CreatedContent[] = [
        {
          type: "art",
          title: "Calming Moonscape",
          content: "üåô A serene night scene with soft blue and silver tones. The moon gently illuminates calm waters, surrounded by peaceful stars that twinkle like hope."
        },
        {
          type: "story",
          title: "A Journey to Inner Peace",
          content: "Once upon a time, there was a young person who felt overwhelming emotions. They discovered that like waves in the ocean, feelings come and go. By breathing deeply and focusing on the present moment, they found their inner calm..."
        },
        {
          type: "music",
          title: "Gentle Rain Melody",
          content: "üéµ Soft piano notes like raindrops... Dm - F - C - G... A gentle rhythm that matches your heartbeat when you're finding peace."
        },
        {
          type: "tip",
          title: "Wellness Tip",
          content: "When emotions feel overwhelming, try the 4-7-8 breathing technique: Inhale for 4 counts, hold for 7, exhale for 8. This activates your body's relaxation response."
        }
      ];

      setCreatedContent(mockContent);
      setIsGenerating(false);

      // Track usage for achievements
      trackSandboxCreation();
    }, 2000);

    console.log("Generating content for emotion:", emotion);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault();
      generateContent();
    }
  };

  return (
    <div className="max-w-4xl mx-auto space-y-6 relative">
      {/* Animated Background */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="sandbox-bg">
          <div className="floating-shapes">
            {[...Array(12)].map((_, i) => (
              <div
                key={i}
                className="floating-shape"
                style={{
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  animationDelay: `${Math.random() * 8}s`,
                  animationDuration: `${6 + Math.random() * 4}s`,
                }}
              >
                {['üé®', '‚ú®', 'üåà', 'üé≠', 'üé™', 'üå∏'][Math.floor(Math.random() * 6)]}
              </div>
            ))}
          </div>
          <div className="art-patterns">
            {[...Array(8)].map((_, i) => (
              <div
                key={i}
                className="art-pattern"
                style={{
                  left: `${Math.random() * 100}%`,
                  top: `${Math.random() * 100}%`,
                  animationDelay: `${Math.random() * 10}s`,
                }}
              />
            ))}
          </div>
        </div>
      </div>

      {/* Input Section */}
      <Card className="bg-gradient-to-r from-accent/10 to-primary/10 backdrop-blur-sm border border-accent/30 relative z-10">
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-accent">
            <Palette className="h-5 w-5" />
            üé® Emotion Sandbox - Creative Expression Therapy
          </CardTitle>
          <CardDescription>
            ‚ú® Transform your feelings into beautiful, calming creations. Turn any emotion into art, music, stories, and wellness tips.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex gap-3">
            <Input
              value={emotion}
              onChange={(e) => setEmotion(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="I feel... (e.g., lonely, anxious, overwhelmed, excited)"
              disabled={isGenerating}
              data-testid="input-emotion"
              className="flex-1"
            />
            <Button
              onClick={generateContent}
              disabled={!emotion.trim() || isGenerating}
              data-testid="button-generate-content"
              className="hover-elevate"
            >
              {isGenerating ? (
                <>
                  <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                  Creating...
                </>
              ) : (
                <>
                  <Sparkles className="h-4 w-4 mr-2" />
                  Create
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Loading State */}
      {isGenerating && (
        <Card className="border-primary/20">
          <CardContent className="p-8 text-center">
            <div className="space-y-4">
              <Loader2 className="h-12 w-12 animate-spin text-primary mx-auto" />
              <h3 className="text-lg font-semibold">Creating your personalized content...</h3>
              <p className="text-muted-foreground">
                AI is transforming "{emotion}" into calming art, stories, and wellness tips
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Generated Content */}
      {createdContent.length > 0 && !isGenerating && (
        <Tabs defaultValue="art" className="w-full">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="art" data-testid="tab-art">
              <Palette className="h-4 w-4 mr-1" />
              Art
            </TabsTrigger>
            <TabsTrigger value="story" data-testid="tab-story">
              <BookOpen className="h-4 w-4 mr-1" />
              Story
            </TabsTrigger>
            <TabsTrigger value="music" data-testid="tab-music">
              <Music className="h-4 w-4 mr-1" />
              Music
            </TabsTrigger>
            <TabsTrigger value="tip" data-testid="tab-tip">
              <Sparkles className="h-4 w-4 mr-1" />
              Tip
            </TabsTrigger>
          </TabsList>

          {createdContent.map((content) => (
            <TabsContent key={content.type} value={content.type}>
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    {content.type === "art" && <Palette className="h-5 w-5 text-accent" />}
                    {content.type === "story" && <BookOpen className="h-5 w-5 text-primary" />}
                    {content.type === "music" && <Music className="h-5 w-5 text-chart-3" />}
                    {content.type === "tip" && <Sparkles className="h-5 w-5 text-chart-4" />}
                    {content.title}
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <p className="text-muted-foreground leading-relaxed whitespace-pre-line">
                    {content.content}
                  </p>
                  <div className="mt-4 flex gap-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => console.log(`Saved ${content.type}: ${content.title}`)}
                      data-testid={`button-save-${content.type}`}
                      className="hover-elevate"
                    >
                      Save to Journal
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => console.log(`Shared ${content.type}: ${content.title}`)}
                      data-testid={`button-share-${content.type}`}
                      className="hover-elevate"
                    >
                      Share
                    </Button>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          ))}
        </Tabs>
      )}

      {/* Example Prompts */}
      {createdContent.length === 0 && !isGenerating && (
        <Card className="border-muted">
          <CardHeader>
            <CardTitle className="text-sm">Try these example emotions:</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex flex-wrap gap-2">
              {["lonely", "anxious", "excited", "overwhelmed", "peaceful", "frustrated"].map((example) => (
                <Button
                  key={example}
                  variant="outline"
                  size="sm"
                  onClick={() => setEmotion(example)}
                  data-testid={`button-example-${example}`}
                  className="hover-elevate"
                >
                  {example}
                </Button>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Animated Background Styles */}
      <style>{`
        .sandbox-bg {
          position: absolute;
          inset: 0;
          background: radial-gradient(circle at 20% 80%, 
            rgba(168, 85, 247, 0.1) 0%, 
            rgba(59, 130, 246, 0.1) 30%, 
            rgba(16, 185, 129, 0.1) 60%, 
            rgba(245, 158, 11, 0.1) 100%);
          overflow: hidden;
        }

        .floating-shapes {
          position: absolute;
          inset: 0;
        }

        .floating-shape {
          position: absolute;
          font-size: 1.5rem;
          animation: floatArt infinite ease-in-out;
          pointer-events: none;
          opacity: 0.7;
        }

        @keyframes floatArt {
          0%, 100% {
            transform: translateY(0) rotate(0deg) scale(1);
            opacity: 0.4;
          }
          33% {
            transform: translateY(-15px) rotate(120deg) scale(1.1);
            opacity: 0.8;
          }
          66% {
            transform: translateY(-10px) rotate(240deg) scale(0.9);
            opacity: 0.6;
          }
        }

        .art-patterns {
          position: absolute;
          inset: 0;
        }

        .art-pattern {
          position: absolute;
          width: 40px;
          height: 40px;
          background: linear-gradient(45deg, 
            rgba(168, 85, 247, 0.3) 0%, 
            rgba(59, 130, 246, 0.3) 50%, 
            rgba(16, 185, 129, 0.3) 100%);
          border-radius: 50% 20% 50% 20%;
          animation: patternDance 12s infinite ease-in-out;
          pointer-events: none;
        }

        @keyframes patternDance {
          0%, 100% {
            transform: scale(0.5) rotate(0deg);
            opacity: 0.2;
          }
          25% {
            transform: scale(1.2) rotate(90deg);
            opacity: 0.5;
          }
          50% {
            transform: scale(0.8) rotate(180deg);
            opacity: 0.3;
          }
          75% {
            transform: scale(1.1) rotate(270deg);
            opacity: 0.6;
          }
        }
      `}</style>
    </div>
  );
}

client/src/components/GratitudeJar.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Plus, Heart, Sparkles } from "lucide-react";

interface GratitudeNote {
  id: string;
  text: string;
  date: Date;
  color: string;
}

const noteColors = [
  "bg-yellow-100 dark:bg-yellow-900/20 border-yellow-300 dark:border-yellow-700",
  "bg-pink-100 dark:bg-pink-900/20 border-pink-300 dark:border-pink-700",
  "bg-blue-100 dark:bg-blue-900/20 border-blue-300 dark:border-blue-700",
  "bg-green-100 dark:bg-green-900/20 border-green-300 dark:border-green-700",
  "bg-purple-100 dark:bg-purple-900/20 border-purple-300 dark:border-purple-700",
];

export default function GratitudeJar() {
  const [currentNote, setCurrentNote] = useState("");
  const [notes, setNotes] = useState<GratitudeNote[]>([
    // TODO: remove mock data
    {
      id: "1",
      text: "Had a really good conversation with my friend today",
      date: new Date(Date.now() - 24 * 60 * 60 * 1000),
      color: noteColors[0]
    },
    {
      id: "2", 
      text: "Found a new song that made me feel peaceful",
      date: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000),
      color: noteColors[1]
    },
    {
      id: "3",
      text: "My pet made me smile when I was feeling down",
      date: new Date(Date.now() - 3 * 24 * 60 * 60 * 1000),
      color: noteColors[2]
    }
  ]);

  const addGratitudeNote = () => {
    if (!currentNote.trim()) return;

    const newNote: GratitudeNote = {
      id: Date.now().toString(),
      text: currentNote.trim(),
      date: new Date(),
      color: noteColors[Math.floor(Math.random() * noteColors.length)]
    };

    setNotes(prev => [newNote, ...prev]);
    setCurrentNote("");
    console.log("Added gratitude note:", newNote.text);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === "Enter") {
      e.preventDefault();
      addGratitudeNote();
    }
  };

  const getWeeklyCollage = () => {
    const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);
    const weeklyNotes = notes.filter(note => note.date >= weekAgo);

    if (weeklyNotes.length >= 5) {
      return (
        <Card className="border-gold bg-gradient-to-r from-yellow-50 to-orange-50 dark:from-yellow-950/20 dark:to-orange-950/20">
          <CardContent className="p-4 text-center">
            <Sparkles className="h-8 w-8 text-yellow-600 mx-auto mb-2" />
            <h3 className="font-semibold text-yellow-800 dark:text-yellow-200 mb-2">
              Weekly Gratitude Collage! ‚ú®
            </h3>
            <p className="text-sm text-yellow-700 dark:text-yellow-300">
              Amazing! You've collected {weeklyNotes.length} moments of gratitude this week.
              Your jar is glowing with positivity!
            </p>
          </CardContent>
        </Card>
      );
    }
    return null;
  };

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      {/* Header */}
      <div className="text-center space-y-2">
        <div className="text-6xl mb-4">ü´ô</div>
        <h2 className="text-2xl font-bold text-primary">Your Gratitude Jar</h2>
        <p className="text-muted-foreground">
          Drop one good thing from your day into your virtual jar
        </p>
      </div>

      {/* Add New Note */}
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">What made you smile today?</CardTitle>
          <CardDescription>
            Even small moments count - a kind word, a beautiful sunset, or a good meal
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex gap-3">
            <Input
              value={currentNote}
              onChange={(e) => setCurrentNote(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="I'm grateful for..."
              data-testid="input-gratitude"
              className="flex-1"
            />
            <Button
              onClick={addGratitudeNote}
              disabled={!currentNote.trim()}
              data-testid="button-add-gratitude"
              className="hover-elevate"
            >
              <Plus className="h-4 w-4 mr-1" />
              Add
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Weekly Collage */}
      {getWeeklyCollage()}

      {/* Gratitude Notes */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Heart className="h-5 w-5 text-destructive" />
            Your Gratitude Collection ({notes.length} notes)
          </CardTitle>
        </CardHeader>
        <CardContent>
          <ScrollArea className="h-[400px] pr-4">
            <div className="space-y-3">
              {notes.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <div className="text-4xl mb-4">‚ú®</div>
                  <p>Your gratitude jar is waiting for its first note!</p>
                  <p className="text-sm mt-2">Start by adding something you're grateful for today.</p>
                </div>
              ) : (
                notes.map((note) => (
                  <div
                    key={note.id}
                    className={`p-4 rounded-lg border-2 ${note.color} transform hover:scale-105 transition-transform cursor-pointer`}
                    data-testid={`gratitude-note-${note.id}`}
                  >
                    <p className="text-sm font-medium text-foreground mb-2">
                      {note.text}
                    </p>
                    <p className="text-xs text-muted-foreground">
                      {note.date.toLocaleDateString()} ‚Ä¢ {note.date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </p>
                  </div>
                ))
              )}
            </div>
          </ScrollArea>
        </CardContent>
      </Card>

      {/* Gratitude Tips */}
      <Card className="border-muted">
        <CardHeader>
          <CardTitle className="text-sm">Gratitude Tips üí°</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-1 text-sm text-muted-foreground">
            <p>‚Ä¢ Try to add at least one note per day</p>
            <p>‚Ä¢ Look for small moments: a smile, good weather, a tasty meal</p>
            <p>‚Ä¢ Review your jar when you're feeling down</p>
            <p>‚Ä¢ Gratitude gets easier with practice!</p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/GroundingGame.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { CheckCircle, Circle } from "lucide-react";

const steps = [
  { count: 5, sense: "see", prompt: "5 things you can see around you" },
  { count: 4, sense: "touch", prompt: "4 things you can touch" },
  { count: 3, sense: "hear", prompt: "3 things you can hear" },
  { count: 2, sense: "smell", prompt: "2 things you can smell" },
  { count: 1, sense: "taste", prompt: "1 thing you can taste" },
];

export default function GroundingGame() {
  const [currentStep, setCurrentStep] = useState(0);
  const [answers, setAnswers] = useState<string[][]>(Array(5).fill([]).map(() => []));
  const [isComplete, setIsComplete] = useState(false);

  const addAnswer = (stepIndex: number, answer: string) => {
    if (answer.trim()) {
      const newAnswers = [...answers];
      newAnswers[stepIndex] = [...newAnswers[stepIndex], answer.trim()];
      setAnswers(newAnswers);

      // Move to next step if current step is complete
      if (newAnswers[stepIndex].length === steps[stepIndex].count) {
        if (stepIndex < steps.length - 1) {
          setCurrentStep(stepIndex + 1);
        } else {
          setIsComplete(true);
          console.log("Grounding exercise completed");
        }
      }
    }
  };

  const resetGame = () => {
    setCurrentStep(0);
    setAnswers(Array(5).fill([]).map(() => []));
    setIsComplete(false);
    console.log("Grounding game reset");
  };

  const getCurrentInput = () => {
    if (isComplete) return null;

    const step = steps[currentStep];
    const currentAnswers = answers[currentStep];

    return (
      <div className="space-y-2">
        <Input
          placeholder={`Name ${step.count - currentAnswers.length} more...`}
          onKeyPress={(e) => {
            if (e.key === "Enter") {
              addAnswer(currentStep, e.currentTarget.value);
              e.currentTarget.value = "";
            }
          }}
          data-testid={`input-grounding-${step.sense}`}
        />
        <div className="flex flex-wrap gap-1">
          {currentAnswers.map((answer, index) => (
            <span
              key={index}
              className="px-2 py-1 bg-primary/10 text-primary rounded-md text-sm"
            >
              {answer}
            </span>
          ))}
        </div>
      </div>
    );
  };

  if (isComplete) {
    return (
      <Card className="border-green-200 bg-green-50 dark:bg-green-950 dark:border-green-800">
        <CardContent className="p-4 text-center">
          <CheckCircle className="h-12 w-12 text-green-600 mx-auto mb-2" />
          <h3 className="font-semibold text-green-800 dark:text-green-200 mb-2">
            Great job! You're grounded.
          </h3>
          <p className="text-sm text-green-700 dark:text-green-300 mb-4">
            You've successfully completed the 5-4-3-2-1 grounding technique.
          </p>
          <Button
            onClick={resetGame}
            data-testid="button-grounding-restart"
            variant="outline"
            size="sm"
          >
            Try Again
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-4">
      <div className="text-center">
        <h3 className="font-semibold mb-2">5-4-3-2-1 Grounding Technique</h3>
        <p className="text-sm text-muted-foreground">
          This helps you focus on the present moment
        </p>
      </div>

      <div className="space-y-3">
        {steps.map((step, index) => (
          <div key={index} className="flex items-start gap-3">
            <div className="mt-1">
              {index < currentStep || (index === currentStep && answers[index].length === step.count) ? (
                <CheckCircle className="h-5 w-5 text-green-600" />
              ) : index === currentStep ? (
                <Circle className="h-5 w-5 text-primary" />
              ) : (
                <Circle className="h-5 w-5 text-muted-foreground" />
              )}
            </div>
            <div className="flex-1">
              <p className={`font-medium ${index === currentStep ? "text-primary" : index < currentStep ? "text-green-600" : "text-muted-foreground"}`}>
                {step.prompt}
              </p>
              {index === currentStep && getCurrentInput()}
              {index < currentStep && answers[index].length > 0 && (
                <div className="flex flex-wrap gap-1 mt-1">
                  {answers[index].map((answer, answerIndex) => (
                    <span
                      key={answerIndex}
                      className="px-2 py-1 bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200 rounded-md text-sm"
                    >
                      {answer}
                    </span>
                  ))}
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

client/src/components/GrowthTracker.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell } from "recharts";
import { TrendingUp, Calendar, Heart } from "lucide-react";

interface MoodEntry {
  date: string;
  mood: string;
  intensity: number;
  color: string;
}

const moodOptions = [
  { value: "calm", label: "Calm üå∏", color: "#10b981", intensity: 5 },
  { value: "happy", label: "Happy üòÉ", color: "#f59e0b", intensity: 5 },
  { value: "excited", label: "Excited ‚ú®", color: "#8b5cf6", intensity: 4 },
  { value: "neutral", label: "Neutral üòê", color: "#6b7280", intensity: 3 },
  { value: "anxious", label: "Anxious üòü", color: "#ef4444", intensity: 2 },
  { value: "sad", label: "Sad üò¢", color: "#3b82f6", intensity: 2 },
  { value: "stressed", label: "Stressed üò§", color: "#dc2626", intensity: 1 },
];

export default function GrowthTracker() {
  const [selectedMood, setSelectedMood] = useState("");

  // TODO: remove mock data
  const [moodData, setMoodData] = useState<MoodEntry[]>([
    { date: "Mon", mood: "calm", intensity: 5, color: "#10b981" },
    { date: "Tue", mood: "calm", intensity: 5, color: "#10b981" },
    { date: "Wed", mood: "happy", intensity: 5, color: "#f59e0b" },
    { date: "Thu", mood: "calm", intensity: 5, color: "#10b981" },
    { date: "Fri", mood: "stressed", intensity: 1, color: "#dc2626" },
    { date: "Sat", mood: "happy", intensity: 5, color: "#f59e0b" },
    { date: "Sun", mood: "stressed", intensity: 1, color: "#dc2626" },
  ]);

  const addMoodEntry = () => {
    if (!selectedMood) return;

    const mood = moodOptions.find(m => m.value === selectedMood);
    if (!mood) return;

    const today = new Date().toLocaleDateString('en-US', { weekday: 'short' });
    const newEntry: MoodEntry = {
      date: today,
      mood: mood.value,
      intensity: mood.intensity,
      color: mood.color
    };

    // Replace today's entry if it exists, otherwise add new
    const updatedData = moodData.filter(entry => entry.date !== today);
    setMoodData([...updatedData, newEntry]);
    setSelectedMood("");

    console.log("Added mood entry:", newEntry);
  };

  const getMoodCounts = () => {
    const counts = moodData.reduce((acc, entry) => {
      acc[entry.mood] = (acc[entry.mood] || 0) + 1;
      return acc;
    }, {} as Record<string, number>);

    return Object.entries(counts).map(([mood, count]) => {
      const moodOption = moodOptions.find(m => m.value === mood);
      return {
        name: moodOption?.label || mood,
        value: count,
        color: moodOption?.color || "#6b7280"
      };
    });
  };

  const getAIFeedback = () => {
    const calmCount = moodData.filter(entry => entry.mood === "calm").length;
    const happyCount = moodData.filter(entry => entry.mood === "happy").length;
    const stressedCount = moodData.filter(entry => entry.mood === "stressed").length;

    if (calmCount >= 3 && happyCount >= 2) {
      return {
        message: "You are getting better at finding calm before exams. Your emotional balance is improving! üåü",
        type: "positive"
      };
    } else if (stressedCount >= 3) {
      return {
        message: "I notice you've been feeling stressed lately. Remember to use your breathing exercises and take breaks. You're doing great! üíú",
        type: "supportive"
      };
    } else {
      return {
        message: "You're on a wonderful journey of self-awareness. Every day you track is a step toward better emotional health! ‚ú®",
        type: "encouraging"
      };
    }
  };

  const aiCommunication = getAIFeedback();
  const pieData = getMoodCounts();

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Add Mood Entry */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Heart className="h-5 w-5 text-destructive" />
            How are you feeling today?
          </CardTitle>
          <CardDescription>
            Track your daily emotions to see patterns and growth over time
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="flex gap-3">
            <Select value={selectedMood} onValueChange={setSelectedMood}>
              <SelectTrigger className="flex-1" data-testid="select-mood">
                <SelectValue placeholder="Select your current mood..." />
              </SelectTrigger>
              <SelectContent>
                {moodOptions.map((mood) => (
                  <SelectItem key={mood.value} value={mood.value}>
                    {mood.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Button
              onClick={addMoodEntry}
              disabled={!selectedMood}
              data-testid="button-add-mood"
              className="hover-elevate"
            >
              <Calendar className="h-4 w-4 mr-1" />
              Track
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* AI Feedback */}
      <Card className={`border-2 ${aiCommunication.type === 'positive' ? 'border-green-200 bg-green-50 dark:bg-green-950/20' : aiCommunication.type === 'supportive' ? 'border-blue-200 bg-blue-50 dark:bg-blue-950/20' : 'border-purple-200 bg-purple-50 dark:bg-purple-950/20'}`}>
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-sm">
            <TrendingUp className="h-4 w-4" />
            AI Feedback & Insights
          </CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-muted-foreground leading-relaxed">
            {aiCommunication.message}
          </p>
        </CardContent>
      </Card>

      {/* Charts Grid */}
      <div className="grid md:grid-cols-2 gap-6">
        {/* Weekly Mood Chart */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">This Week's Mood Journey</CardTitle>
            <CardDescription>
              Your emotional patterns over the past 7 days
            </CardDescription>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={250}>
              <BarChart data={moodData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" />
                <YAxis domain={[0, 5]} />
                <Tooltip 
                  formatter={(value, name, props) => [
                    `Intensity: ${value}`,
                    moodOptions.find(m => m.value === props.payload.mood)?.label || props.payload.mood
                  ]}
                />
                <Bar dataKey="intensity" fill="#8884d8">
                  {moodData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Bar>
              </BarChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>

        {/* Mood Distribution */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Mood Distribution</CardTitle>
            <CardDescription>
              How often you've experienced each emotion
            </CardDescription>
          </CardHeader>
          <CardContent>
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={pieData}
                  cx="50%"
                  cy="50%"
                  outerRadius={80}
                  dataKey="value"
                  label={({name, value}) => `${name}: ${value}`}
                >
                  {pieData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </CardContent>
        </Card>
      </div>

      {/* Stats Summary */}
      <Card>
        <CardHeader>
          <CardTitle>Weekly Summary</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
            <div>
              <div className="text-2xl font-bold text-green-600">
                {moodData.filter(entry => entry.mood === "calm").length}
              </div>
              <div className="text-sm text-muted-foreground">Calm Days üå∏</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-yellow-600">
                {moodData.filter(entry => entry.mood === "happy").length}
              </div>
              <div className="text-sm text-muted-foreground">Happy Days üòÉ</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-red-600">
                {moodData.filter(entry => entry.mood === "stressed").length}
              </div>
              <div className="text-sm text-muted-foreground">Stressed Days üòü</div>
            </div>
            <div>
              <div className="text-2xl font-bold text-blue-600">
                {moodData.length}
              </div>
              <div className="text-sm text-muted-foreground">Total Tracked</div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/LanguageSwitcher.t.content:
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Globe, ChevronDown } from "lucide-react";
import { useTranslation } from "../contexts/TranslationContext";
import type { TranslationKey } from "@shared/translations";

interface Language {
  code: TranslationKey;
  name: string;
  flag: string;
}

// Limited to Hindi and English only for MindEase
const languages: Language[] = [
  { code: "en", name: "English", flag: "üá∫üá∏" },
  { code: "hi", name: "‡§π‡§ø‡§Ç‡§¶‡•Ä", flag: "üáÆüá≥" },
];

interface LanguageSwitcherProps {
  variant?: "default" | "ghost" | "outline";
  size?: "default" | "sm" | "lg";
}

export default function LanguageSwitcher({ 
  variant = "ghost", 
  size = "default" 
}: LanguageSwitcherProps) {
  const { currentLanguage, changeLanguage, t } = useTranslation();

  const currentLangData = languages.find(lang => lang.code === currentLanguage) || languages[0];

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button 
          variant={variant} 
          size={size}
          data-testid="button-language-switcher"
          className="hover-elevate"
        >
          <Globe className="h-4 w-4 mr-2" />
          <span className="hidden sm:inline">{currentLangData.flag}</span>
          <span className="hidden md:inline ml-1">
            {currentLanguage === "en" ? t.languages.english : t.languages.hindi}
          </span>
          <ChevronDown className="h-3 w-3 ml-1" />
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent align="end" className="min-w-[150px]">
        {languages.map((language) => (
          <DropdownMenuItem
            key={language.code}
            onClick={() => changeLanguage(language.code)}
            className="flex items-center gap-2 cursor-pointer"
            data-testid={`option-language-${language.code}`}
          >
            <span className="text-lg">{language.flag}</span>
            <span>
              {language.code === "en" ? t.languages.english : t.languages.hindi}
            </span>
          </DropdownMenuItem>
        ))}
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

